import {
  CountryWrapper,
  CountryDescription,
  Flag,
  Image,
  CountryTitle,
  CountryCapital,
  CountryDetail,
  Accent,
} from "./CountryInfo.styled";

export const CountryInfo = ({ country }) => {
  if (!country) return;
  const { flag, capital, countryName, id, languages, population } = country;
  return (
    <CountryWrapper key={id}>
      <Flag>
        <Image src={flag} alt={countryName} />
      </Flag>
      <CountryDescription>
        <CountryCapital>
          Capital: <Accent>{capital}</Accent>
        </CountryCapital>

        {/* <CountryTitle>{countryName}</CountryTitle> */}

        <CountryDetail>
          Population: <Accent>{population}</Accent>
        </CountryDetail>

        <CountryDetail>
          Languages: <Accent>{languages.join(", ")}</Accent>
        </CountryDetail>
      </CountryDescription>
    </CountryWrapper>
  );
};
